							game[row][ccol + 1] *= 2;
							game[row][ccol] = 0;
						}

					}
				}
			}
		}
		break;
	}

}

//å¤„ç†è¾“å…¥è¾“å‡ºï¼Œè¿”å›ä¸Šä¸‹å·¦å³
int Input()
{
	//è¯»å–ä¸Šä¸‹å·¦å³å››ä¸ªæ–¹å‘é”®
	int upArrow = 0;
	int downArrow = 0;
	int leftArrow = 0;
	int rightArrow = 0;
	int direction = 0;
	while (true)
	{
		upArrow = GetAsyncKeyState(VK_UP);
		downArrow = GetAsyncKeyState(VK_DOWN);
		leftArrow = GetAsyncKeyState(VK_LEFT);
		rightArrow = GetAsyncKeyState(VK_RIGHT);

		if(upArrow)
		{
			direction = UP;
			break;
		}
		else if(downArrow)
		{
			direction = DOWN;
			break;
		}
		else if(leftArrow)
		{
			direction = LEFT;
			break;
		}
		else if(rightArrow)
		{
			direction = RIGHT;
			break;
		}

		Sleep(100);
	}

	return direction;
}

//åˆ¤æ–­æ¸¸æˆçŠ¶æ€
int Judge()
{
	//èµ¢å¾—æ¸¸æˆ
	for(int i = 0; i < ROW; ++i)
	{
		for(int j = 0; j < COL; ++j)
		{
			if(game[i][j] == 2048)
			{
				return GAME_WIN;
				break;
			}
		}
	}

	//æ¨ªå‘æ£€æŸ¥
	for(int i = 0 ; i < ROW; ++i)
	{
		for(int j = 0; j < COL - 1; ++j)
		{
			if(!game[i][j] || (game[i][j] == game[i][j+1]))
			{
				return GAME_CONTINUE;
				break;
			}
		}
	}
	//çºµå‘æ£€æŸ¥
	for(int j = 0; j< COL; ++j)
	{
		for(int i = 0; i < ROW -1; ++i)
		{
			if(!game[i][j] || (game[i][j] == game[i+1][j]))
			{
				return GAME_CONTINUE;
				break;
			}
		}
	}

	//ä¸ç¬¦åˆä¸Šè¿°ä¸¤ç§çŠ¶å†µï¼Œæ¸¸æˆç»“æŸ
	return GAME_OVER;

}

int main()
{
	//è®¾ç½®ä¸€ä¸ªéšæœºæ•°ç§å­
	srand((unsigned int)time(0));
	CreateNumber();
	CreateNumber();
	Print();
	int direction = 0;
	int gameState = -1;
	while(true)
	{
		direction = Input();

		gameState = Judge();
		if(direction && gameState == GAME_CONTINUE)
		{
			Process(direction);
			CreateNumber();
			Print();
			Sleep(100);
		}
		else if(gameState == GAME_WIN)
		{
			Print();
			cout << "You Winï¼" << endl;
			break;
		}
		else if(gameState == GAME_OVER)
		{
			Print();
			cout <<"You lose!" << endl;
			break;
		}
	}

	return 0;
}
