#include<iostream>
using namespace std;
int factor(int n)
{
    if (n==0|n==1)
        return n;
    return n*factor(n-1);
}
int main ()
{
    int n,m,ans;
    scanf("%d%d",&n,&m);
    ans=factor(m)/(factor(n)*factor(m-n));
    printf("%d",ans);
}
