#include <iostream>
int aa[1000][1000];
using namespace std;
int conbinatorics(int n,int m)
{
    if(aa[n][m])
        return aa[n][m];
    if(m==1)
    {
        return aa[n][m]=n;
    }
    if(n==m)
    {
        return aa[n][m]=1;
    }
    return aa[n][m]=conbinatorics(n-1,m-1)+conbinatorics(n-1,m);
}
int main()
{
    int n,m;
    cin>>n>>m;
    cout <<conbinatorics(n,m)<< endl;
    return 0;
}
