```C++
#include <iostream>
#include <cstdio>
using namespace std;

int main()
{
    int n,i,j=0;
    cin>>n;
    int circle[n+2]={0},status[n+2]={0},via[n+2]={0},flag[n+2]={0};//circleç”¨æ¥å­˜å‚¨è¾“å…¥çš„æ•°æ®ï¼Œstatusç”¨æ¥è®°å½•iä¸ªäººæœ‰å‡ ä¸ªäººä¼šå‘Šè¯‰ä»–ä¿¡æ¯ï¼Œviaç”¨æ¥è®°å½•æŸä¸ªäººæ˜¯å¦è¢«å‘Šè¯‰è¿‡ä¿¡æ¯
    for( i = 1 ; i <= n ; i++ )
    {
        cin>>circle[i];
        status[circle[i]]++;
    }
    for( i = 1 ; i <=  n; i++ )
    {
        if(status[i]==0)
        {
            flag[j++]=i;
            via[i]=1;
        }
    }
    for( i = 0 ; i < j ; i++ )
    {
        status[circle[flag[i]]]--;
        if(status[circle[flag[i]]]==0)
        {
            flag[j++]=circle[flag[i]];
            via[circle[flag[i]]]=1;
        }
    }
    int ans=n,temp;
    for( i = 1 ; i <= n ; i++ )
    {
        if( via[i]==0 && circle[i]!=0 )
        {
            via[i]=1;
            temp=1;
            j=circle[i];
            while(!via[j])
            {
                via[j]=1;
                j=circle[j];
                temp++;
            }
            if(temp<ans)
                ans=temp;
        }
    }
    printf("%d",ans);
    return 0;
}
