					{
						printf("recv server: \r\n %s\r\n", buf);
					}
					memset(buf, 0, 1024);
				}
				break;
			case 2:

				printf("%s", sendbuf2);
				if (send(sock, sendbuf2, strlen(sendbuf2), 0) != -1)
				{
					//æ¥æ”¶æœåŠ¡ç«¯æ•°æ®
					//Sleep(1000);
