					if (recv(sock, buf, 1024, 0) > 0)
					{
						printf("recv server: \n %s\n", buf);
					}
					memset(buf, 0, 1024);
				}
				break;

			}
			if (sw == 3)
				break;
		}

	}
	else
	{
		printf("can not connect server\n");
	}

	//å…³é—­socket
	closesocket(sock);

	//å…³é—­winsockæœåŠ¡
	WSACleanup();
	getchar();
	return 0;
