```C++
#include <iostream>
#include<algorithm>
#include<string>
using namespace std;

class Student
{
public:
    string name;
    int score;
};

bool compare(Student a,Student b)
{
    return a.score>b.score;
}
int main()
{
    Student stu[3]={"stu1",70,"stu2",80,"stu3",60};
    sort(stu,stu+3,compare);
    for(int i=0;i<3;i++)
    {
        cout<<stu[i].name<<" "<<stu[i].score<<endl;
    }
    return 0;
}
