## libcurl å‘èµ· post è¯·æ±‚å’Œ get è¯·æ±‚

ä¸ºäº†æ–¹ä¾¿æ“ä½œï¼Œå»ºè®®ä½¿ç”¨linuxæ“ä½œç³»ç»Ÿï¼ŒWindowså¯èƒ½ä¼šé¢ä¸´ä¸€ç³»åˆ—çš„ libcurl åŠå…¶åº“çš„ç¼–è¯‘ï¼Œä¸ç†Ÿæ‚‰çš„æƒ…å†µä¸‹ï¼Œæ“ä½œæ˜¯ååˆ†ç¹ççš„ã€‚

ä¸‹é¢çš„å®ç°æ˜¯å‚è€ƒäº† libcurl çš„sampleæ¥å®ç°çš„
```C
#include <stdio.h>
#include <string.h>
#include <curl/curl.h>
#include <memory.h>
#include <assert.h>
#include <stdlib.h>

#ifndef __cplusplus
typedef int bool;
#define true 1
#define false 0
#endif

#ifdef __cplusplus
extern "C" {
#endif // __cplusplus


/*å­˜å‚¨ä»¥POSTæ–¹å¼éœ€è¦å‘é€çš„æ•°æ®*/
struct Request
{
    char* readptr;
    size_t sizeleft;
};


struct Response
{
    unsigned char* response;
    size_t size;
};

/*å‘é€æ•°æ®çš„å›è°ƒå‡½æ•°*/
static size_t send_callback(char* dest, size_t size, size_t nmemb, void* userp)
{
    struct Request* wt = (struct Request*)userp;
    size_t buffer_size = size * nmemb;

    if (wt->sizeleft) {
        /* copy as much as possible from the source to the destination */
        size_t copy_this_much = wt->sizeleft;
        if (copy_this_much > buffer_size)
            copy_this_much = buffer_size;
        memcpy(dest, wt->readptr, copy_this_much);

        wt->readptr += copy_this_much;
        wt->sizeleft -= copy_this_much;
        return copy_this_much; /* we copied this many bytes */
    }

    return 0; /* no more data left to deliver */
}

/*æ¥æ”¶æ•°æ®çš„å›è°ƒå‡½æ•°*/
static size_t receive_callback(void* data, size_t size, size_t nmemb, void* userp)
{
    size_t realsize = size * nmemb;
    struct Response* mem = (struct Response*)userp;

    unsigned char* ptr = (char*)realloc(mem->response, mem->size + realsize + 1);
    if (ptr == NULL)
        return 0;  /* out of memory! */

    mem->response = ptr;
    memcpy(&(mem->response[mem->size]), data, realsize);
    mem->size += realsize;
    mem->response[mem->size] = 0;

    return realsize;
}

/*å¦‚æœå®šä¹‰äº† USE_SPECCIFIC_CACERT_PEM ï¼Œé‚£ä¹ˆå‡½æ•°ç¬¬ä¸€ä¸ªå‚æ•°åº”è¯¥æŒ‡å‘ä¸€ä¸ª catcert.pem è¯ä¹¦*/
#ifdef USE_SPECCIFIC_CACERT_PEM
int handle_https_callback(const char* certpem, const char* url, const char* parameter, size_t size_send, void** responsedata, size_t* size_receive, char** content_type, bool post, bool verbose)
#else
int handle_https_callback(const char* url, const char* parameter, size_t size_send, void** responsedata, size_t* size_receive, char** content_type, bool post, bool verbose)
#endif // USE_SPECCIFIC_CACERT_PEM
{
    assert(url);
    CURL* curl;
    CURLcode res;
    struct Request request;
    struct Response response = { 0 };
    struct curl_slist* headers = NULL;
    int result = 0;

    /*å¦‚æœparameteréç©ºï¼Œé‚£ä¹ˆæœ‰æ•°æ®è¦å‘é€ï¼Œå°±ç”³è¯·ç›¸åº”çš„å‘é€æ•°æ®çš„ç©ºé—´*/
    bool hasParameter = false;
    bool hasParameter = false;
    if (parameter != NULL)
    {
        hasParameter = true;
        size_t len = strlen(parameter);
        size_t min = len < size_send ? len : size_send;
        request.readptr = (char *)parameter;
        request.sizeleft = min;
    }

    res = curl_global_init(CURL_GLOBAL_DEFAULT);
    if (res != CURLE_OK)
    {
        fprintf(stderr, "curl_global_init() failed: %s\n", curl_easy_strerror(res));
        return 1;
    }
    curl = curl_easy_init();
    if (curl)
    {
        curl_easy_setopt(curl, CURLOPT_URL, url);
        if (post)
        {
            curl_easy_setopt(curl, CURLOPT_POST, 1L);
        }

        /*å¦‚æœæœ‰æ•°æ®éœ€è¦å‘é€ï¼Œå°±è®¾ç½®ç›¸åº”çš„æ•°æ®å‘é€å›è°ƒå‡½æ•°ï¼Œè¦å‘é€çš„æ•°æ®å’Œæ•°æ®é•¿åº¦*/
        if (hasParameter)
        {
            curl_easy_setopt(curl, CURLOPT_READFUNCTION, send_callback);
            curl_easy_setopt(curl, CURLOPT_READDATA, &request);
            curl_easy_setopt(curl, CURLOPT_POSTFIELDSIZE, (long)request.sizeleft);
        }

        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, receive_callback);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, (void*)&response);

        headers = curl_slist_append(headers, "Content-Type: application/json");

        /*å¦‚æœå®šä¹‰äº† USE_SPECCIFIC_CACERT_PEM ï¼Œé‚£ä¹ˆè®¾ç½®SSLè®¤è¯è¯ä¹¦*/
#ifdef USE_SPECCIFIC_CACERT_PEM
        curl_easy_setopt(curl, CURLOPT_CAINFO, certpem)
#elif defined(DISABLE_CURL_SSL_VERIFY) // å¦‚æœå®šä¹‰äº† DISABLE_CURL_SSL_VERIFY è¯ä¹¦è®¤è¯ï¼Œé‚£ä¹ˆå°±è®¾ç½®curlä¸å¼ºåˆ¶è®¤è¯æœåŠ¡å™¨
        curl_easy_setopt(curl, CURLOPT_SSL_VERIFYPEER, false);
        curl_easy_setopt(curl, CURLOPT_SSL_VERIFYHOST, 0);
#endif // USE_SPECCIFIC_CACERT_PEM


        if (verbose)
        {
            curl_easy_setopt(curl, CURLOPT_VERBOSE, 1L);
        }

        res = curl_easy_perform(curl);
        if (res == CURLE_OK)
        {
            long http_code = 0;
            curl_easy_getinfo(curl, CURLINFO_RESPONSE_CODE, &http_code);
            if (http_code == 200)
            {
                /*curl_easy_cleanup(curl) ä¼šé‡Šæ”¾æ‰ tmp_content_type*/
                char* tmp_content_type = NULL;
                curl_easy_getinfo(curl, CURLINFO_CONTENT_TYPE, &tmp_content_type);
                if(tmp_content_type)
                {
                    size_t tmplen = strlen(tmp_content_type);
                    *content_type = malloc(tmplen + 1);
                    memset(*content_type, 0, tmplen + 1);
                    memcpy(*content_type, tmp_content_type, tmplen);
                }
                /*ç”¨æˆ·éœ€è¦è‡ªè¡Œé‡Šæ”¾ responsedata */
                *responsedata = response.response;
                *size_receive = response.size;
            }
            else
            {
                result = -2;
                fprintf(stderr, "net work error with http code %ld", http_code);
            }
        }
        else
        {
            result = 2;
            fprintf(stderr, "curl_easy_perform() failed: %s\n", curl_easy_strerror(res));
        }

        curl_slist_free_all(headers);
        curl_easy_cleanup(curl);
    }
    curl_global_cleanup();
    return result;
}

#ifdef __cplusplus
    extern "C" }
#endif // __cplusplus
