        i++;
    }
    i=0;
    while(i<m)
    {
        scanf("%d%d",&command_1,&command_2);
        command_2=command_2%n;
        if(pan[ans]==command_1) //顺时针走
        {
            ans-=command_2;
            if(ans<=0)
                ans+=n;
        }
        else  //逆时针针走
        {
            ans+=command_2;
            if(ans>=n)
                ans-=n;
        }
        i++;
    }
    printf("%s",job[ans]);
}
```
